package pattenPrograms;

public class Patten1 {

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
		int n=5;
		for(int row=0;row<n;row++)//outerloop row
		{
			for(int coloum=0;coloum<n;coloum++)//innerloop coloum
			{
			System.out.print("*"+" ");	
			}
			System.out.println();
		}

	}

}
