package pattenPrograms;

public class Patten7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=5;
		 int a=5;
		for(int i=0;i<n;i++)
		{
			for(int j=0;j<n;j++)
			{
			System.out.print(a+" ");
			a--;
			}
			
			System.out.println();
			a=5;
		}


	}

}
